import React, { useState } from "react";

function App() {
  const [inputText, setInputText] = useState("");
  const [audioUrl, setAudioUrl] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!inputText.trim()) return alert("Please enter some text!");
    setLoading(true);

    try {
      const response = await fetch("http://localhost:3000/api/voice-chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ text: inputText }),
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const blob = await response.blob(); // audio blob
      const url = URL.createObjectURL(blob); // create audio URL
      setAudioUrl(url);
    } catch (error) {
      console.error("Error:", error);
      alert("Error: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>ðŸŽ¤ Voice AI Chat (Gemini + ElevenLabs)</h2>

      <textarea
        style={styles.textarea}
        rows="3"
        placeholder="Type your message..."
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
      />

      <button style={styles.button} onClick={handleSend} disabled={loading}>
        {loading ? "Generating..." : "Send"}
      </button>

      {audioUrl && (
        <div style={{ marginTop: "20px" }}>
          <p>AI Voice Reply:</p>
          <audio controls src={audioUrl} />
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "600px",
    margin: "40px auto",
    textAlign: "center",
    fontFamily: "Arial, sans-serif",
    padding: "20px",
  },
  heading: {
    marginBottom: "20px",
  },
  textarea: {
    width: "100%",
    padding: "10px",
    fontSize: "16px",
  },
  button: {
    marginTop: "10px",
    padding: "10px 20px",
    fontSize: "16px",
    backgroundColor: "#007bff",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
};

export default App;
