const THEME_KEY = "voice-ai-theme";

// Helper to create a new empty conversation
function createNewConversation(index = 1) {
  const id =
    window.crypto && window.crypto.randomUUID
      ? window.crypto.randomUUID()
      : String(Date.now());
  return {
    id,
    title: `New Chat ${index}`,
    createdAt: Date.now(),
    messages: [],
  };
}

function App() {
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [inputText, setInputText] = useState("");
  const [loading, setLoading] = useState(false);
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef(null);
  const [openMenuId, setOpenMenuId] = useState(null);
  const [theme, setTheme] = useState("light"); // üåó theme state
  const [inputRows, setInputRows] = useState(2); // textarea grows 2 -> 8 rows
  const [selectedFile, setSelectedFile] = useState(null);

  const isDark = theme === "dark";

  // üîä Helper: convert base64 ‚Üí audio URL
  const base64ToAudioUrl = (base64) => {
    const binary = atob(base64);
    const len = binary.length;
    const bytes = new Uint8Array(len);
    for (let i = 0; i < len; i++) {
      bytes[i] = binary.charCodeAt(i);
    }
    const blob = new Blob([bytes], { type: "audio/mpeg" });
    return URL.createObjectURL(blob);
  };

  // üß† Load conversations from localStorage on first render
  useEffect(() => {
    try {
      const stored = localStorage.getItem(STORAGE_KEY);
      if (stored) {
        const parsed = JSON.parse(stored);
        if (Array.isArray(parsed) && parsed.length > 0) {
          setConversations(parsed);
          setCurrentConversationId(parsed[0].id);
        } else {
          const firstConv = createNewConversation(1);
          setConversations([firstConv]);
          setCurrentConversationId(firstConv.id);
        }
      } else {
        const firstConv = createNewConversation(1);
        setConversations([firstConv]);
        setCurrentConversationId(firstConv.id);
      }
    } catch (e) {
      console.error("Failed to parse stored conversations:", e);
      const firstConv = createNewConversation(1);
      setConversations([firstConv]);
      setCurrentConversationId(firstConv.id);
    }
  }, []);

  // üíæ Save conversations to localStorage whenever they change
  useEffect(() => {
    if (conversations.length > 0) {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(conversations));
    } else {
      localStorage.removeItem(STORAGE_KEY);
    }
  }, [conversations]);

  // üé® Load saved theme
  useEffect(() => {
    const storedTheme = localStorage.getItem(THEME_KEY);
    if (storedTheme === "light" || storedTheme === "dark") {
      setTheme(storedTheme);
    }
  }, []);

  // üé® Persist theme
  useEffect(() => {
    localStorage.setItem(THEME_KEY, theme);
  }, [theme]);

  // üßæ Get current conversation + messages
  const currentConversation =
    conversations.find((c) => c.id === currentConversationId) ||
    conversations[0];

  const messages = currentConversation?.messages || [];

  // Helper to update messages of current conversation
  const updateCurrentConversationMessages = (updater) => {
    if (!currentConversation) return;
    setConversations((prev) =>
      prev.map((c) =>
        c.id === currentConversation.id
          ? { ...c, messages: updater(c.messages || []) }
          : c
      )
    );
  };

  // ‚ûï Create a new chat
  const handleNewChat = () => {
    const nextIndex = conversations.length + 1;
    const newConv = createNewConversation(nextIndex);
    setConversations((prev) => [...prev, newConv]);
    setCurrentConversationId(newConv.id);
    setInputText("");
  };

  // ‚úèÔ∏è Rename chat
  const handleRenameChat = (conv) => {
    const newTitle = window.prompt("Enter new chat name:", conv.title);
    if (!newTitle || !newTitle.trim()) return;
    setConversations((prev) =>
      prev.map((c) => (c.id === conv.id ? { ...c, title: newTitle.trim() } : c))
    );
  };

  // üßπ Clear a specific chat by id
  const clearChatById = (convId) => {
    setConversations((prev) =>
      prev.map((c) => (c.id === convId ? { ...c, messages: [] } : c))
    );
    if (convId === currentConversationId) {
      setInputText("");
    }
  };

  // üóë Delete a specific chat by id
  const deleteChatById = (convId) => {
    setConversations((prev) => {
      const filtered = prev.filter((c) => c.id !== convId);

      if (filtered.length === 0) {
        const newConv = createNewConversation(1);
        setCurrentConversationId(newConv.id);
        return [newConv];
      }

      if (convId === currentConversationId) {
        setCurrentConversationId(filtered[0].id);
      }

      return filtered;
    });
  };

  // üî• Send text to backend ‚Üí get replyText + audioBase64
  const handleSend = async (textToSend) => {
  const text = textToSend ?? inputText;

  if (!text.trim() && !selectedFile) {
    alert("Enter a message or upload a file.");
    return;
  }

  setLoading(true);
  setInputText("");

  try {
    let response;

    if (selectedFile) {
      const formData = new FormData();
      formData.append("file", selectedFile);
      formData.append("question", text || "Summarize this file");

      response = await fetch("http://localhost:3000/api/file-chat", {
        method: "POST",
        body: formData,
      });

      setSelectedFile(null);
    } else {
      response = await fetch("http://localhost:3000/api/voice-chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text }),
      });
    }


      const contentType = response.headers.get("Content-Type");
      console.log("Response content-type:", contentType);

      if (!response.ok) {
        let errMsg = `Server error ${response.status}: `;
        try {
          const errData = await response.json();
          console.error("Backend error details:", errData);
          if (errData.error) errMsg += errData.error;
        } catch (e) {
          console.error("Could not parse error JSON:", e);
        }
        alert(errMsg);
        return;
      }

      const data = await response.json();
      console.log("Backend JSON:", data);

      if (!data.audioBase64 || !data.replyText) {
        alert("Server did not return audioBase64 / replyText");
        return;
      }

      const audioUrl = base64ToAudioUrl(data.audioBase64);

      updateCurrentConversationMessages((msgs) => [
        ...msgs,
        {
          role: "ai",
          text: data.replyText,
          audioUrl,
          timestamp: Date.now(),
        },
      ]);
    } catch (error) {
      console.error("Fetch error:", error);
      alert("Network error: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // üéô Start listening from mic (browser STT)
  const startListening = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert(
        "Speech recognition is not supported in this browser. Try Chrome or Edge on desktop."
      );
      return;
    }

    if (listening) return;

    const recognition = new SpeechRecognition();
    recognition.lang = "en-IN";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.onstart = () => {
      console.log("Speech recognition started");
      setListening(true);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error:", event.error);
      setListening(false);
      alert("Speech recognition error: " + event.error);
    };

    recognition.onend = () => {
      console.log("Speech recognition stopped");
      setListening(false);
    };

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      console.log("Transcript:", transcript);
      setInputText(transcript);
      handleSend(transcript);
    };

    recognitionRef.current = recognition;
    recognition.start();
  };

  const stopListening = () => {
    if (recognitionRef.current && listening) {
      recognitionRef.current.stop();
      setListening(false);
    }
  };

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  return (
    <div
      style={{
        ...styles.appWrapper,
        backgroundColor: isDark ? "#020617" : "#f5f5f5",
        color: isDark ? "#e5e7eb" : "#111827",
      }}
    >
      {/* üß≠ Sidebar with chats */}
      <div
        style={{
          ...styles.sidebar,
          borderRight: isDark ? "1px solid #1f2937" : "1px solid #ddd",
          backgroundColor: isDark ? "#020617" : "#ffffff",
        }}
      >
        <div style={styles.sidebarHeader}>
          <h3 style={{ margin: 0 }}>Chats</h3>
          <button style={styles.newChatButton} onClick={handleNewChat}>
            Ôºã New
          </button>
        </div>

        <div style={styles.chatList}>
          {conversations.map((conv) => (
            <div
              key={conv.id}
              style={{
                ...styles.chatListItem,
                backgroundColor:
                  conv.id === currentConversationId
                    ? isDark
                      ? "#111827"
                      : "#e3f2fd"
                    : "transparent",
              }}
              onClick={() => setCurrentConversationId(conv.id)}
            >
              <div
                style={{
                  flex: 1,
                  textAlign: "left",
                  overflow: "hidden",
                  textOverflow: "ellipsis",
                  whiteSpace: "nowrap",
                }}
              >
                {conv.title}
              </div>

              {/* 3-dot menu button */}
              <button
                style={{
                  ...styles.menuButton,
                  color: isDark ? "#e5e7eb" : "#111827",
                }}
                onClick={(e) => {
                  e.stopPropagation();
                  setOpenMenuId(openMenuId === conv.id ? null : conv.id);
                }}
              >
                ‚ãÆ
              </button>

              {/* Dropdown menu */}
              {openMenuId === conv.id && (
                <div
                  style={{
                    ...styles.contextMenu,
                    backgroundColor: isDark ? "#111827" : "#ffffff",
                    borderColor: isDark ? "#374151" : "#ddd",
                  }}
                  onClick={(e) => e.stopPropagation()}
                >
                  <button
                    style={{
                      ...styles.contextMenuItem,
                      color: isDark ? "#e5e7eb" : "#111827",
                    }}
                    onClick={() => {
                      handleRenameChat(conv);
                      setOpenMenuId(null);
                    }}
                  >
                    ‚úèÔ∏è Rename
                  </button>

                  <button
                    style={{
                      ...styles.contextMenuItem,
                      color: isDark ? "#e5e7eb" : "#111827",
                    }}
                    onClick={() => {
                      if (window.confirm("Clear all messages in this chat?")) {
                        clearChatById(conv.id);
                        setOpenMenuId(null);
                      }
                    }}
                  >
                    üßπ Clear
                  </button>

                  <button
                    style={{
                      ...styles.contextMenuItem,
                      color: "#f97373",
                    }}
                    onClick={() => {
                      if (
                        window.confirm(
                          `Delete chat "${conv.title}" permanently?`
                        )
                      ) {
                        deleteChatById(conv.id);
                        setOpenMenuId(null);
                      }
                    }}
                  >
                    üóëÔ∏è Delete
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* üì± Main chat area */}
      <div style={styles.mainArea}>
        {/* Top header / logo + theme toggle */}
        <div style={styles.headerBar}>
          <div style={styles.logoArea}>
            <span style={{ fontSize: "20px", marginRight: "6px" }}>üîä</span>
            <span style={{ fontWeight: "bold" }}>Voice AI Chat</span>
          </div>

          {/* üåó Theme toggle icon (replaces text) */}
          <button
            onClick={toggleTheme}
            style={{
              ...styles.themeToggleButton,
              backgroundColor: isDark ? "#111827" : "#e5e7eb",
              color: isDark ? "#facc15" : "#0f172a",
            }}
            title={
              theme === "light"
                ? "Switch to dark theme"
                : "Switch to light theme"
            }
          >
            {theme === "light" ? "üåû" : "üåô"}
          </button>
        </div>

        {/* Waveform / listening indicator */}
        {listening && (
          <div className="listening-indicator">
            <span className="listening-dot" />
          </div>
        )}

        {/* Scrollable chat area taking full available height */}
        <div
          style={{
            ...styles.chatContainer,
            backgroundColor: isDark ? "#020617" : "#fafafa",
            borderColor: isDark ? "#1f2937" : "#ddd",
          }}
        >
          {messages.length === 0 && (
            <div
              style={{
                color: isDark ? "#9ca3af" : "#777",
                fontSize: "14px",
              }}
            >
              No messages yet. Start speaking or type a message below.
            </div>
          )}
          {messages.map((m, idx) => (
            <div
              key={idx}
              style={{
                ...styles.message,
                alignSelf: m.role === "user" ? "flex-end" : "flex-start",
                backgroundColor:
                  m.role === "user"
                    ? isDark
                      ? "#2563eb"
                      : "#007bff"
                    : isDark
                    ? "#111827"
                    : "#f1f0f0",
                color: m.role === "user" ? "#fff" : isDark ? "#e5e7eb" : "#000",
              }}
            >
              <div style={styles.messageText}>
                <strong>{m.role === "user" ? "You: " : "AI: "}</strong>
                {m.text}
              </div>
              {m.role === "ai" && m.audioUrl && (
                <audio
                  controls
                  src={m.audioUrl}
                  style={{ marginTop: "6px", width: "100%" }}
                />
              )}
            </div>
          ))}
        </div>

        {/* Bottom composer fixed at bottom of main area */}
        <div
          style={{
            ...styles.composerWrapper,
            borderTop: isDark ? "1px solid #1f2937" : "1px solid #ddd",
          }}
        >
          <div style={styles.composerInner}>


            <textarea
              style={{
                ...styles.textarea,
                backgroundColor: isDark ? "#020617" : "#ffffff",
                color: isDark ? "#e5e7eb" : "#111827",
                borderColor: isDark ? "#1f2937" : "#ddd",
              }}
              rows={inputRows} // üëà dynamic rows
              placeholder="Type your message, or use the mic..."
              value={inputText}
              onChange={(e) => {
                const value = e.target.value;
                setInputText(value);

                // count lines and grow between 2 and 8 rows
                const lineCount = value.split("\n").length;
                const nextRows = Math.max(2, Math.min(8, lineCount));
                setInputRows(nextRows);
              }}
              onKeyDown={(e) => {
                if (e.key === "Enter" && !e.shiftKey) {
                  e.preventDefault();
                  if (!loading) {
                    handleSend();
                  }
                }
              }}
            />
            <input
              type="file"
              accept=".pdf,.txt"
              onChange={(e) => setSelectedFile(e.target.files[0])}
            />

            {/* Mic icon button */}
            <button
              style={{
                ...styles.micButton,
                backgroundColor: listening ? "#dc3545" : "#22c55e",
              }}
              onClick={listening ? stopListening : startListening}
              disabled={loading}
              title={listening ? "Stop listening" : "Start voice input"}
            >
              {listening ? "üõë" : "üéô"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

