    position: "absolute",
    right: "6px",
    top: "26px",
    border: "1px solid #ddd",
    borderRadius: "6px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
    zIndex: 10,
    display: "flex",
    flexDirection: "column",
    minWidth: "150px",
  },
  contextMenuItem: {
    padding: "6px 10px",
    fontSize: "13px",
    textAlign: "left",
    border: "none",
    background: "transparent",
    cursor: "pointer",
  },
  mainArea: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    height: "100vh",
    padding: "12px 16px",
    boxSizing: "border-box",
  },
  headerBar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "8px",
  },
  logoArea: {
    display: "flex",
    alignItems: "center",
    gap: "4px",
  },
  themeToggleButton: {
    border: "none",
    borderRadius: "999px",
    padding: "4px 10px",
    cursor: "pointer",
    fontSize: "16px",
  },
  chatContainer: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    gap: "8px",
    overflowY: "auto",
    marginBottom: "8px",
    padding: "8px",
    border: "1px solid #ddd",
